// 카테고리별 키워드 시드
const categories = [
  {
    category: 'AI',
    keywords: ['AI 도구 추천', 'ChatGPT 활용법', 'Claude 사용법', 'AI 이미지 생성', 'AI 코딩', 'AI 자동화', 'AI 부업'],
    coupangCategory: 'tech'
  },
  {
    category: 'Dev',
    keywords: ['프로그래밍 입문', 'React 튜토리얼', 'Python 자동화', 'Next.js 배포', 'Supabase 가이드', '개발자 생산성'],
    coupangCategory: 'books'
  },
  {
    category: 'Review',
    keywords: ['노트북 추천', '모니터 비교', '키보드 리뷰', '개발자 장비', '태블릿 추천', '이어폰 비교'],
    coupangCategory: 'electronics'
  }
];

// 오늘 날짜 기반 카테고리 로테이션
const today = new Date();
const dayIndex = today.getDate() % categories.length;
const selected = categories[dayIndex];

// 카테고리 내에서 랜덤 키워드 선택
const keywordIndex = Math.floor(Math.random() * selected.keywords.length);
const keyword = selected.keywords[keywordIndex];

return [{
  json: {
    category: selected.category,
    keyword: keyword,
    coupangCategory: selected.coupangCategory,
    date: today.toISOString().split('T')[0],
    dateKR: `${today.getFullYear()}년 ${today.getMonth() + 1}월 ${today.getDate()}일`
  }
}];

const topic = $('Select Topic & Category').first().json;
const searchResults = $input.first().json;

// 검색 결과에서 참고 정보 추출
let references = '';
if (searchResults.results && searchResults.results.length > 0) {
  references = searchResults.results
    .slice(0, 3)
    .map((r, i) => `${i + 1}. ${r.title}: ${(r.text || '').substring(0, 200)}`)
    .join('\n');
}

return [{
  json: {
    ...topic,
    references: references || '최신 트렌드 정보를 참고하여 작성해주세요.'
  }
}];

