parameters.jsCode:
const topic = $('Select Topic & Category').first().json;
const response = $input.first().json;

// Claude 응답에서 JSON 추출
let content;
try {
  const text = response.content[0].text;
  // JSON 블록 추출 (```json ... ``` 또는 순수 JSON)
  const jsonMatch = text.match(/```json\s*([\s\S]*?)\s*```/) || text.match(/\{[\s\S]*\}/);
  const jsonStr = jsonMatch ? (jsonMatch[1] || jsonMatch[0]) : text;
  content = JSON.parse(jsonStr);
} catch (e) {
  // 파싱 실패 시 기본값
  content = {
    title: `${topic.keyword} - 완전 가이드 (${topic.date})`,
    description: `${topic.keyword}에 대한 최신 정보와 실용적인 가이드를 제공합니다.`,
    tags: [topic.keyword, topic.category, '가이드', '2026'],
    content: response.content[0].text
  };
}

// 쿠팡 추천 상품 매핑
const coupangProducts = {
  tech: [
    { title: '로지텍 MX Keys S 무선 키보드', url: 'https://link.coupang.com/re/AFFSDP?lptag=AF0000000' },
    { title: 'LG 울트라와이드 34인치 모니터', url: 'https://link.coupang.com/re/AFFSDP?lptag=AF0000000' }
  ],
  books: [
    { title: '모던 자바스크립트 Deep Dive', url: 'https://link.coupang.com/re/AFFSDP?lptag=AF0000000' },
    { title: '클린 코드 (로버트 C. 마틴)', url: 'https://link.coupang.com/re/AFFSDP?lptag=AF0000000' }
  ],
  electronics: [
    { title: '삼성 갤럭시북4 프로 16인치', url: 'https://link.coupang.com/re/AFFSDP?lptag=AF0000000' },
    { title: '애플 맥북 에어 M3', url: 'https://link.coupang.com/re/AFFSDP?lptag=AF0000000' }
  ]
};

const products = coupangProducts[topic.coupangCategory] || coupangProducts.tech;

// Slug 생성 (날짜-제목)
const slug = `${topic.date}-${content.title
  .replace(/[^a-zA-Z0-9가-힣\s]/g, '')
  .replace(/\s+/g, '-')
  .substring(0, 50)
  .toLowerCase()}`;

// Frontmatter + Content 조합
const coupangYaml = products.map(p => `  - title: "${p.title}"\n    url: "${p.url}"`).join('\n');

const markdown = `---
title: "${content.title}"
description: "${content.description}"
pubDate: ${topic.date}
category: "${topic.category}"
tags: ${JSON.stringify(content.tags)}
author: "TechFlow"
coupangLinks:
${coupangYaml}
---

${content.content}`;

return [{
  json: {
    slug: slug,
    filename: `${slug}.md`,
    markdown: markdown,
    title: content.title,
    category: topic.category,
    date: topic.date
  }
}];

const post = $('Format Blog Post').first().json;

// 성공 로그 기록
const result = {
  status: 'success',
  title: post.title,
  slug: post.slug,
  category: post.category,
  date: post.date,
  publishedAt: new Date().toISOString(),
  message: `Blog post "${post.title}" published successfully.`
};

return [{ json: result }];
